import { writable } from 'svelte/store';

// 创建策略提示 store
function createStrategyHint() {
    const { subscribe, set } = writable({
        strategy: null,
        position: null,
        value: null
    });

    return {
        subscribe,
        // 设置当前使用的策略、位置和值
        setHint: (strategy, position, value) => {
            set({
                strategy,
                position,
                value
            });
        },
        // 清除提示
        clear: () => {
            set({
                strategy: null,
                position: null,
                value: null
            });
        }
    };
}

export const strategyHint = createStrategyHint();
