import { STRATEGIES } from '../strategies/index'
import { strategyHint } from '../../stores/strategy'

const currentStrategy = strategyHint
class Scheduler {
    // find a aproximate strategy
    findStrategy () {
        for (const name in STRATEGIES) {
            const strategy = STRATEGIES[name]
            if (strategy.check()) {
                currentStrategy.setHint(strategy)
                console.log('strategy found:', strategy)
                break
            }
        }
    }
}


export const strategyScheduler = new Scheduler()